implementation.source:

float3 ETSShaderDiffuse(
    float2 uv,
    float anisotropy,
    float sharpness,
    float bend,
    float bulge,
    float rotation,
    float normalSmooth,
    float intensityGain,
    float softness
)
{
    float average = (anisotropy+sharpness+normalSmooth) / 3.0f;
    return average;
}


