implementation.source:

float ETSShaderDiffuse(
    float3 editWorldPosition[50],
    float anisotropy[50],
    float sharpness[50],
    float bend[50],
    float bulge[50],
    float rotation[50],
    float normalSmooth[50],
    float intensityGain[50],
    float softness[50],
    int editNum
)
{
    float res = 0.0f;
    for (int i=0;i<editNum;++i)
    {
        res += sharpness[i];
    }
    res = res / (float)editNum;
    return res;
}


