implementation.source:

float3 ETSToonFragment(
    float3 BaseColor,
    float3 DiffuseShading,
    float3 diffuseI,
    float ShadeIntensityRatio)
{
    float4 col = BaseColor;
    col *= lerp(ShadeIntensityRatio, 1.0f, DiffuseShading.r) * diffuseI;
    return col;
}


float3 ETSToonFragment(
    float3 BaseColor,
    float3 DiffuseShading,
    float3 diffuseI,
    float ShadeIntensityRatio)
{
    float4 col = BaseColor;
    col *= lerp(ShadeIntensityRatio, 1.0f, DiffuseShading.r) * diffuseI;
    return col;
}


vec3 ETSToonFragment(
    vec3 BaseColor,
    vec3 DiffuseShading,
    vec3 diffuseI,
    float ShadeIntensityRatio)
{
    vec4 col = BaseColor;
    col *= lerp(ShadeIntensityRatio, 1.0f, DiffuseShading.r) * diffuseI;    
    return col;
}
